import { useState } from 'react';
import {
  MarkGithubIcon,
  TriangleDownIcon,
  PencilIcon,
  SearchIcon,
  GlobeIcon,
} from '@primer/octicons-react';
import {
  Box,
  Button,
  Heading,
  IconButton,
  Text,
  TextInput,
} from '@primer/react';
import { useHostname } from '../../context/HostnameContext';

export function Navigation() {
  const { hostname, updateHostname } = useHostname();
  const [edit, setEdit] = useState(false);

  const handleEditButton = () => {
    setEdit(!edit);
  };

  const sx = {
    color: 'fg.muted',
  };

  return (
    <Box
      sx={{
        display: 'flex',
        bg: 'canvas.subtle',
        borderBottom: '1px solid',
        borderColor: 'border.default',
        p: 3,
        justifyContent: 'space-between',
      }}
    >
      <Box
        sx={{
          display: 'flex',
          gap: '3',
        }}
      >
        <Box
          sx={{
            display: 'flex',
            gap: '12px',
            alignItems: 'center',
          }}
        >
          <MarkGithubIcon size={32} />
          <Heading
            sx={{
              fontSize: '2',
            }}
          >
            GitHub Docs
          </Heading>
        </Box>
        <Box
          sx={{
            height: '32px',
            width: '1px',
            bg: 'border.default',
            display: ['none', null, 'block'],
          }}
        />
        <Box
          sx={{
            display: ['none', null, 'flex'],
            gap: '2',
            alignItems: 'center',
          }}
        >
          <Text
            sx={{
              fontSize: '1',
              color: 'fg.muted',
              fontWeight: 'semibold',
            }}
          >
            <Text
              sx={{
                color: 'fg.default',
              }}
            >
              Version:{' '}
            </Text>
            <Text
              as='span'
              sx={{
                ml: 1,
                fontWeight: 'inherit',
              }}
            >
              Enterprise Server 3.12
            </Text>
          </Text>
          <TriangleDownIcon />
        </Box>
        <Box
          sx={{
            display: ['none', null, 'flex'],
            gap: '2',
            alignItems: edit ? 'flex-end' : 'center',
            ml: 2,
          }}
        >
          <Text
            sx={{
              fontSize: '1',
              color: 'fg.muted',
              fontWeight: 'semibold',
            }}
          >
            <Text
              sx={{
                color: 'fg.default',
              }}
            >
              Host Name:{' '}
            </Text>
            {edit ? (
              <TextInput
                aria-label='Search'
                name='search'
                size='small'
                placeholder='Search GitHub Docs'
                value={hostname}
                onChange={(e) => {
                  updateHostname(e.target.value);
                }}
                sx={{
                  bg: 'canvas.subtle',
                  color: 'fg.muted',
                  display: 'inline-flex',
                  ml: 2,
                }}
              />
            ) : (
              <Text
                as='span'
                sx={{
                  ml: 1,
                  fontWeight: 'inherit',
                }}
              >
                {hostname}
              </Text>
            )}
          </Text>
          <Button
            variant='invisible'
            size='small'
            onClick={handleEditButton}
          >
            {edit ? 'Save' : 'Edit'}
          </Button>
        </Box>
      </Box>

      <Box
        sx={{
          display: 'flex',
          gap: '3',
          alignItems: 'center',
        }}
      >
        <TextInput
          trailingVisual={SearchIcon}
          aria-label='Search'
          name='search'
          placeholder='Search GitHub Docs'
          sx={{
            bg: 'canvas.subtle',
            color: 'fg.muted',
            display: ['none', 'flex'],
          }}
        />

        <IconButton
          sx={sx}
          icon={GlobeIcon}
          aria-label='Open inbox'
        />
        <Box
          sx={{
            height: '32px',
            width: '1px',
            bg: 'border.default',
          }}
        />
        <Button>Sign up</Button>
      </Box>
    </Box>
  );
}
